options.customCss:
/* ChatGPT-like theme */
:root {
  /* Brand-ish */
  --chat--color-primary: #10a37f;
  --chat--color-primary-shade-50: #0e8f71;
  --chat--color-primary-shade-100: #0b745d;
  --chat--color-secondary: #19c37d;
  --chat--color-secondary-shade-50: #16aa6f;

  /* Neutrals (ChatGPT vibe) */
  --chat--color-white: #ffffff;
  --chat--color-light: #f7f7f8;
  --chat--color-light-shade-50: #ececf1;
  --chat--color-light-shade-100: #d9d9e3;
  --chat--color-medium: #b4b8bf;
  --chat--color-dark: #202123;
  --chat--color-disabled: #8e8ea0;
  --chat--color-typing: #6e6e80;

  /* Base layout */
  --chat--spacing: 0.875rem;
  --chat--border-radius: 0.75rem;
  --chat--transition-duration: 0.15s;
  --chat--font-family: ui-sans-serif, system-ui, -apple-system, "Segoe UI", Roboto, Inter, "Helvetica Neue", Arial, Noto Sans, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", sans-serif;

  /* Window */
  --chat--window--width: 720px;
  --chat--window--height: 80vh;
  --chat--window--bottom: var(--chat--spacing);
  --chat--window--right: var(--chat--spacing);
  --chat--window--z-index: 9999;
  --chat--window--border: 1px solid var(--chat--color-light-shade-50);
  --chat--window--border-radius: 1rem;
  --chat--window--margin-bottom: var(--chat--spacing);

  /* Header */
  --chat--header-height: auto;
  --chat--header--padding: var(--chat--spacing);
  --chat--header--background: var(--chat--color-white);
  --chat--header--color: var(--chat--color-dark);
  --chat--header--border-top: none;
  --chat--header--border-bottom: 1px solid var(--chat--color-light-shade-50);
  --chat--header--border-left: none;
  --chat--header--border-right: none;
  --chat--heading--font-size: 1rem;
  --chat--subtitle--font-size: .875rem;
  --chat--subtitle--line-height: 1.4;

  /* Messages */
  --chat--message--font-size: 1rem;
  --chat--message--padding: calc(var(--chat--spacing) * .9) var(--chat--spacing);
  --chat--message--border-radius: var(--chat--border-radius);
  --chat--message-line-height: 1.6;
  --chat--message--margin-bottom: .75rem;

  --chat--message--bot--background: var(--chat--color-white);
  --chat--message--bot--color: var(--chat--color-dark);
  --chat--message--bot--border: 1px solid var(--chat--color-light-shade-50);

  --chat--message--user--background: #eaf7f2;
  --chat--message--user--color: #0b4738;
  --chat--message--user--border: 1px solid #cbe9de;

  --chat--message--pre--background: #0f172a; /* dark code blocks */
  --chat--messages-list--padding: 1.25rem;

  /* Toggle */
  --chat--toggle--size: 64px;
  --chat--toggle--width: var(--chat--toggle--size);
  --chat--toggle--height: var(--chat--toggle--size);
  --chat--toggle--border-radius: 50%;
  --chat--toggle--background: var(--chat--color-primary);
  --chat--toggle--hover--background: var(--chat--color-primary-shade-50);
  --chat--toggle--active--background: var(--chat--color-primary-shade-100);
  --chat--toggle--color: var(--chat--color-white);

  /* Input */
  --chat--textarea--height: 56px;
  --chat--textarea--max-height: 40rem;
  --chat--input--font-size: 1rem;
  --chat--input--border: 1px solid var(--chat--color-light-shade-100);
  --chat--input--border-radius: 1rem;
  --chat--input--padding: .875rem 1rem;
  --chat--input--background: var(--chat--color-white);
  --chat--input--text-color: var(--chat--color-dark);
  --chat--input--line-height: 1.5;
  --chat--input--placeholder--font-size: .95rem;
  --chat--input--border-active: 1px solid var(--chat--color-primary);
  --chat--input--left--panel--width: 2rem;

  /* Buttons */
  --chat--button--color: var(--chat--color-white);
  --chat--button--background: var(--chat--color-primary);
  --chat--button--padding: .5rem .875rem;
  --chat--button--border-radius: .5rem;
  --chat--button--hover--color: var(--chat--color-white);
  --chat--button--hover--background: var(--chat--color-primary-shade-50);
  --chat--close--button--color-hover: var(--chat--color-primary);

  /* Send & file buttons (minimal) */
  --chat--input--send--button--background: transparent;
  --chat--input--send--button--color: var(--chat--color-disabled);
  --chat--input--send--button--background-hover: var(--chat--color-light-shade-50);
  --chat--input--send--button--color-hover: var(--chat--color-primary);

  --chat--input--file--button--background: transparent;
  --chat--input--file--button--color: var(--chat--color-disabled);
  --chat--input--file--button--background-hover: var(--chat--color-light-shade-50);
  --chat--input--file--button--color-hover: var(--chat--color-dark);

  /* Body/footer */
  --chat--body--background: var(--chat--color-light);
  --chat--footer--background: var(--chat--color-white);
  --chat--footer--color: var(--chat--color-dark);
}

/* --- ChatGPT-like cards & widths --- */
.chat-messages { padding: var(--chat--messages-list--padding); }
.chat-message {
  max-width: 72ch;
  margin: 0 auto var(--chat--message--margin-bottom);
  padding: var(--chat--message--padding);
  border-radius: var(--chat--message--border-radius);
  line-height: var(--chat--message-line-height);
  font-size: var(--chat--message--font-size);
  box-shadow: 0 1px 0 rgba(0,0,0,.04);
}
.chat-message--bot {
  background: var(--chat--message--bot--background);
  color: var(--chat--message--bot--color);
  border: var(--chat--message--bot--border);
}
.chat-message--user {
  background: var(--chat--message--user--background);
  color: var(--chat--message--user--color);
  border: var(--chat--message--user--border);
}

/* Code blocks (dark, like ChatGPT) */
.chat-message pre,
.chat-message code {
  background: var(--chat--message--pre--background);
  color: #e2e8f0;
  border-radius: .5rem;
  border: 1px solid #1e293b;
}
.chat-message pre {
  padding: .875rem 1rem;
  overflow: auto;
}
.chat-message code { padding: .1rem .35rem; }

/* Header */
.chat-header {
  background: var(--chat--header--background);
  color: var(--chat--header--color);
  border-bottom: var(--chat--header--border-bottom);
  padding: var(--chat--header--padding);
}

/* Input row */
.chat-input {
  padding: .75rem;
  background: var(--chat--footer--background);
  border-top: 1px solid var(--chat--color-light-shade-50);
}
.chat-input textarea {
  width: 100%;
  background: var(--chat--input--background);
  color: var(--chat--input--text-color);
  border: var(--chat--input--border);
  border-radius: var(--chat--input--border-radius);
  padding: var(--chat--input--padding);
  line-height: var(--chat--input--line-height);
  font-size: var(--chat--input--font-size);
  outline: none;
  transition: border-color var(--chat--transition-duration);
}
.chat-input textarea:focus {
  border: var(--chat--input--border-active);
  box-shadow: 0 0 0 3px rgba(16,163,127,.15);
}
.chat-input .send,
.chat-input .attach {
  background: var(--chat--input--send--button--background);
  color: var(--chat--input--send--button--color);
  border-radius: .5rem;
}
.chat-input .send:hover,
.chat-input .attach:hover {
  background: var(--chat--input--send--button--background-hover);
  color: var(--chat--input--send--button--color-hover);
}

/* Optional: auto dark mode to match ChatGPT */
@media (prefers-color-scheme: dark) {
  :root {
    --chat--color-light: #0a0a0b;
    --chat--color-light-shade-50: #1f1f22;
    --chat--color-light-shade-100: #2a2a2e;
    --chat--color-dark: #ececf1;

    --chat--body--background: #0a0a0b;
    --chat--footer--background: #0a0a0b;
    --chat--header--background: #0a0a0b;

    --chat--message--bot--background: #111113;
    --chat--message--bot--color: #ececf1;
    --chat--message--bot--border: 1px solid #222226;

    --chat--message--user--background: #0d2b24;
    --chat--message--user--color: #c6fff0;
    --chat--message--user--border: 1px solid #124236;

    --chat--input--background: #111113;
    --chat--input--text-color: #ececf1;
    --chat--input--border: 1px solid #222226;
    --chat--input--border-active: 1px solid var(--chat--color-primary);

    --chat--message--pre--background: #0b1220;
  }
  .chat-message { box-shadow: none; }
}

