parameters.jsCode:
// n8n Code node (JavaScript). Input: items[].json.text
// Output: items[].json.text_md (use this in the Text Splitter)

return items.map(item => {
  let t = ($('Extract PDF Text').first().json.text ?? "").toString();

  // Normalize newlines + add page separators if present
  t = t.replace(/\r\n?/g, "\n").replace(/\f/g, "\n\n---\n\n");

  // De-hyphenate only when a line-break splits a lowercase word
  t = t.replace(/([a-z])-\n([a-z])/g, "$1$2");

  // Promote major policy sections to Markdown headings
  const headingRules = [
    // SECTION I, SECTION II, numeric/lettered sections, etc.
    { re: /(^|\n)\s*(SECTION\s+[A-Z0-9.\-–]+[^\n]*)/g, level: "## " },
    { re: /(^|\n)\s*(ENDORSEMENT[^\n]*)/g,                 level: "## " },
    { re: /(^|\n)\s*(AMENDATORY[^\n]*)/g,                  level: "## " },
    { re: /(^|\n)\s*(SCHEDULES?|DEFINITIONS|EXCLUSIONS|CONDITIONS|COVERAGE|LIMITS)([^\n]*)/g, level: "## " },
    // Common ISO/AAIS/carrier form headers
    { re: /(^|\n)\s*((?:CG|IL)\s?\d{2}\s?\d{2}[^\n]*)/g,   level: "### " },
    { re: /(^|\n)\s*(HO-\d+[^\n]*)/g,                      level: "### " }
  ];

  for (const { re, level } of headingRules) {
    t = t.replace(re, (m, p1, p2, p3="") => `${p1}\n${level}${p2}${p3}`);
  }

  // Collapse excessive blank lines
  t = t.replace(/\n{3,}/g, "\n\n");

  item.json.text_md = t;     // new field to split on
  return item;
});
