/**
 * n8n Code node — SINGLE OUTPUT (universal, NON-install intent)
 * Input:  $('On form submission').first().json.Topic
 * Output: 1 summary item + K shortlist items (strongest only)
 *
 * Notes:
 * - Excludes install/setup/deploy/devops-y terms by default (EXCLUDE_REGEX)
 * - No brand baked in; base tokens come from Topic (REQUIRE_BASE_TERM keeps relevance)
 */

const PICK              = 12;   // final shortlist size
const CONCURRENCY       = 6;
const JITTER_MS         = 40;

let   QUICK_FLOOR       = 0.55; // discard weak suggestions
const REQUIRE_BASE_TERM = true; // keep suggestions that contain at least one base token

// ----- Universal EXCLUDES (install/setup/devops/admin-y stuff) -----
const EXCLUDE_REGEX = /\b(install|setup|set[-\s]?up|deploy|deployment|configure|provision|self[-\s]?host|local|docker|compose|kubernetes|k8s|helm|reverse\s?proxy|coolify|nginx|systemd|service|ssl|tls|certificate|homebrew|brew|node\s?js|pip|apt|yum|choco|wsl|wsl2)\b/i;

// (Optional) quality guards
const MIN_LEN = 3;              // min characters for a suggestion
const MAX_LEN = 100;            // max characters
const MAX_WORDS = 16;           // avoid very long queries

// ---------- helpers ----------
const canon = (s) =>
  String(s || '')
    .toLowerCase()
    .replace(/[^\p{L}\p{N}\s]+/gu, ' ')
    .replace(/\s+/g, ' ')
    .trim();

const tokenize = (s) => (canon(s).match(/\p{L}[\p{L}\p{N}\+\.-]*/gu) || []);

const includesAny = (s, arr) => arr.some(t => s.includes(t.toLowerCase()));

function significantBaseTokens(topic) {
  const stop = new Set([
    'how','to','for','and','or','the','a','an','in','on','of','with','by','from','at','vs',
    'tutorial','guide','best','top','ideas','2025','2024','2023','beginner','advanced','pro',
  ]);
  return tokenize(topic).filter(t => t.length >= 3 && !stop.has(t));
}

async function fetchSuggest(q) {
  const url = `https://suggestqueries.google.com/complete/search?client=youtube&ds=yt&q=${encodeURIComponent(q)}`;
  try {
    const res = await this.helpers.httpRequest({ method:'GET', url, json:false, headers:{'User-Agent':'n8n'} });
    const text = typeof res === 'string' ? res : String(res);
    const start = text.indexOf('['), end = text.lastIndexOf(']');
    if (start === -1 || end === -1) return { query:q, suggestions:[] };
    let arr;
    try { arr = JSON.parse(text.slice(start, end + 1)); } catch { return { query:q, suggestions:[] }; }
    const query = Array.isArray(arr) ? arr[0] : q;
    const listRaw = Array.isArray(arr?.[1]) ? arr[1] : [];
    const suggestions = [];
    for (let i = 0; i < listRaw.length; i++) {
      const s = typeof listRaw[i] === 'string' ? listRaw[i] : (Array.isArray(listRaw[i]) ? listRaw[i][0] : '');
      if (s) suggestions.push({ text:s, rank:i+1 });
    }
    return { query, suggestions };
  } catch {
    return { query:q, suggestions:[] };
  }
}

// Modest permutation set (keeps calls low but explores)
function* permutations(base) {
  yield base; // exact
  // expand with a–m then 0–3 (light exploration)
  for (const ch of 'abcdefghijklm') yield `${base} ${ch}`;
  for (const d of '0123') yield `${base} ${d}`;
  // semantic modifiers that are NOT install-y (learning/usage oriented)
  const mods = ['ideas', 'examples', 'beginner', 'tutorial', 'tips', 'tricks', 'automation', 'workflow', 'project', 'use cases', 'shorts'];
  for (const m of mods) yield `${base} ${m}`;
}

async function runPool(tasks, limit) {
  const out = new Array(tasks.length);
  let i = 0;
  const workers = Array.from({ length: Math.min(limit, tasks.length) }, async () => {
    while (i < tasks.length) {
      const idx = i++;
      out[idx] = await tasks[idx]();
      await new Promise(r => setTimeout(r, JITTER_MS));
    }
  });
  await Promise.all(workers);
  return out;
}

// ---------- main ----------
const baseInput = ($('On form submission').first().json.Topic || '').trim();
if (!baseInput) {
  return [{ json: { stream: 'error', message: 'Missing keyword Topic from On form submission' } }];
}
const base = canon(baseInput);
const baseTokens = significantBaseTokens(baseInput);

// Build & fetch
const queries = Array.from(permutations(base));
const tasks = queries.map(q => () => fetchSuggest.call(this, q));
const results = await runPool(tasks, CONCURRENCY);

// Aggregate
const byQuery = {};
const stats = new Map(); // canonical text -> {count,sumRank,firstQuery,originals:Set}
let totalOccurrences = 0;
let maxRankObserved = 0;

for (const r of results) {
  if (!r || !r.suggestions) continue;
  byQuery[r.query] = r.suggestions.map(o => o.text);

  for (const { text, rank } of r.suggestions) {
    const tCanon = canon(text);
    if (!tCanon) continue;

    // ---- Universal filters ----
    if (tCanon.length < MIN_LEN || tCanon.length > MAX_LEN) continue;
    if ((tCanon.split(' ').length) > MAX_WORDS) continue;
    if (EXCLUDE_REGEX.test(tCanon)) continue; // drop install/setup/devops
    if (REQUIRE_BASE_TERM && baseTokens.length && !includesAny(tCanon, baseTokens)) continue;

    const prev = stats.get(tCanon) || { count:0, sumRank:0, firstQuery:r.query, originals:new Set() };
    prev.count += 1;
    prev.sumRank += rank;
    prev.originals.add(text);
    stats.set(tCanon, prev);
    totalOccurrences += 1;
    if (rank > maxRankObserved) maxRankObserved = rank;
  }
}

// Compute scores
let weightedAvgRank = 0;
const detailed = [];
for (const [textCanon, { count, sumRank, firstQuery, originals }] of stats.entries()) {
  const avgRank = sumRank / count;
  weightedAvgRank += sumRank;
  const rankScore = 1 - (avgRank / (maxRankObserved || 10)); // 0–1
  const occurrences = count;
  const quickScore = 0.7*rankScore + 0.3*Math.min(1, occurrences/5);

  // shortest original for display
  const display = Array.from(originals).sort((a,b)=>a.length-b.length)[0] || textCanon;

  detailed.push({
    text: display,
    textCanon,
    occurrences,
    avgRank: +avgRank.toFixed(3),
    rankScore: +rankScore.toFixed(3),
    quickScore: +quickScore.toFixed(3),
    firstSeenQuery: firstQuery,
  });
}
weightedAvgRank = totalOccurrences ? (weightedAvgRank / totalOccurrences) : 0;
const overallRankScore = maxRankObserved ? +(1 - (weightedAvgRank / maxRankObserved)).toFixed(3) : 0;

// Filter by quality floor + diversity
let shortlistedMap = detailed
  .filter(r => r.quickScore >= QUICK_FLOOR)
  .reduce((acc, r) => {
    // Diversity: drop near-dupes differing only by numbers/years
    const stem = r.textCanon
      .replace(/\b(20\d{2}|19\d{2})\b/g, '')
      .replace(/\b(v?\d+(\.\d+)*)\b/g, '')
      .trim();
    if (!acc.has(stem)) acc.set(stem, r);
    return acc;
  }, new Map());

let shortlisted = Array.from(shortlistedMap.values())
  .sort((a,b) => b.quickScore - a.quickScore)
  .slice(0, PICK);

// Soft backfill if too few (without re-introducing install-y terms)
const MIN_TARGET = 8;
if (shortlisted.length < MIN_TARGET) {
  const have = new Set(shortlisted.map(x => x.textCanon));
  const pool = detailed
    .filter(r => r.quickScore >= 0.42)      // softer floor
    .sort((a,b) => b.quickScore - a.quickScore);
  for (const cand of pool) {
    if (have.has(cand.textCanon)) continue;
    // re-apply core excludes & base relevance
    if (EXCLUDE_REGEX.test(cand.textCanon)) continue;
    if (REQUIRE_BASE_TERM && baseTokens.length && !includesAny(cand.textCanon, baseTokens)) continue;
    shortlisted.push(cand);
    have.add(cand.textCanon);
    if (shortlisted.length >= Math.min(PICK, MIN_TARGET)) break;
  }
}

// Build output (single stream)
const itemsOut = [];

// Summary
itemsOut.push({
  json: {
    stream: 'summary',
    keyword: baseInput,
    canon: base,
    suggestBreadth: stats.size,
    avgRank: +weightedAvgRank.toFixed(3),
    rankScore: overallRankScore,
    requested: queries.length,
    params: {
      PICK, QUICK_FLOOR, REQUIRE_BASE_TERM, CONCURRENCY,
      excludes: 'install/setup/devops terms',
    },
    // debug fields (optional):
    // suggestionsDetailed: detailed.sort((a,b)=>b.quickScore-a.quickScore),
    // byQuery,
  }
});

// Shortlist (for deep YT querying)
for (const r of shortlisted) {
  itemsOut.push({
    json: {
      stream: 'shortlist',
      keyword: r.text,
      rankScore: r.rankScore,
      occurrences: r.occurrences,
      avgRank: r.avgRank,
      quickScore: r.quickScore,
      source: 'autosuggest',
      parentTopic: baseInput, // keep original Topic for grouping downstream
    }
  });
}

return itemsOut;

// Code node — Global Budget Gate (shape-robust)

// ---------- knobs ----------
const MAX_GLOBAL        = 60;
const MIN_PER_KEYWORD   = 2;
const MAX_PER_KEYWORD   = 10;
const MIN_VPD_BASELINE  = 0.5;     // ignore very cold unless fresh
const MAX_AGE_DAYS_HARD = 1095;    // drop if also cold (>3y)
const STRONG_VPD_BONUS  = 12;
const QS_WEIGHT         = 20;
const CHANNEL_REPEAT_PENALTY = 0.85;

// ---------- helpers ----------
const num = (v,d=0)=>{ const n=Number(v); return Number.isFinite(n)?n:d; };
const daysSince = (iso)=>{ const d=new Date(iso||0); return isNaN(d)?9e9:Math.max(0,(Date.now()-d)/86400000); };
const canon = (s)=> String(s||'').toLowerCase().replace(/[^\p{L}\p{N}\s]+/gu,' ').replace(/\s+/g,' ').trim();

function getKwKey(x){ return (x.keyword?.keyword || x.keyword || x.parentTopic || '(unknown)').toString(); }
function ensureGate(it){ it.json.gate = it.json.gate || { index:0, priority:0, passBasic:true, keepPerKeyword:false, keepGlobal:false, reason:[] }; if (!Array.isArray(it.json.gate.reason)) it.json.gate.reason=[]; return it.json.gate; }

// ---- Normalize any input row to a *video* (or null if not a video) ----
function asVideoRow(it){
  const j = it.json || {};

  // 1) Already-normalized (your own shape)
  if (j.id && j.title && (j.publishedAt || j.video?.publishedAt)) {
    return {
      id: j.id,
      title: j.title,
      publishedAt: j.publishedAt || j.video?.publishedAt || null,
      views: num(j.views ?? j.video?.views, 0),
      likeCount: num(j.likeCount ?? j.video?.likeCount, 0),
      commentCount: num(j.commentCount ?? j.video?.commentCount, 0),
      durationSeconds: num(j.durationSeconds ?? j.video?.durationSeconds, 0),
      channel: j.channel || { Id: j.channelId || null, Name: j.channelTitle || null, subscriberCount: num(j.subscriberCount, 0) },
      keyword: j.keyword,
      parentTopic: j.parentTopic || j.keyword?.parentTopic,
      quickScore: num(j.quickScore ?? j.keyword?.quickScore, 0),
      viewsPerDay: num(j.viewsPerDay ?? j.video?.viewsPerDay, NaN),
      _src: 'normalized',
      _it: it
    };
  }

  // 2) videos.list shape
  if (j.kind === 'youtube#video' || (j.snippet && j.statistics && j.contentDetails && j.id)) {
    return {
      id: j.id,
      title: j.snippet?.title || '',
      publishedAt: j.snippet?.publishedAt || null,
      views: num(j.statistics?.viewCount, 0),
      likeCount: num(j.statistics?.likeCount, 0),
      commentCount: num(j.statistics?.commentCount, 0),
      durationSeconds: (()=>{
        const m = (j.contentDetails?.duration || '').match(/PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+)S)?/);
        if (!m) return 0; const h=+m[1]||0, mm=+m[2]||0, s=+m[3]||0; return h*3600+mm*60+s;
      })(),
      channel: { Id: j.snippet?.channelId, Name: j.snippet?.channelTitle, subscriberCount: num(j.channel?.subscriberCount, 0) },
      keyword: j.keyword,
      parentTopic: j.parentTopic || j.keyword?.parentTopic,
      quickScore: num(j.quickScore ?? j.keyword?.quickScore, 0),
      viewsPerDay: NaN,
      _src: 'videos.list',
      _it: it
    };
  }

  // 3) search.list shape (must be a VIDEO, not channel/playlist)
  if (j.id?.kind === 'youtube#video' && j.id?.videoId) {
    return {
      id: j.id.videoId,
      title: j.snippet?.title || '',
      publishedAt: j.snippet?.publishedAt || null,
      views: num(j.statistics?.viewCount, 0),
      likeCount: num(j.statistics?.likeCount, 0),
      commentCount: num(j.statistics?.commentCount, 0),
      durationSeconds: 0,
      channel: { Id: j.snippet?.channelId, Name: j.snippet?.channelTitle, subscriberCount: num(j.channel?.subscriberCount, 0) },
      keyword: j.keyword,
      parentTopic: j.parentTopic || j.keyword?.parentTopic,
      quickScore: num(j.quickScore ?? j.keyword?.quickScore, 0),
      viewsPerDay: NaN,
      _src: 'search.list',
      _it: it
    };
  }

  // 4) channel/playlist or unknown — skip
  // e.g., your snippet shows kind: youtube#channel → ignore
  return null;
}

function computeVpdFallback(v){
  const age = Math.max(1, Math.floor(daysSince(v.publishedAt)) || 1);
  const vpdRaw = num(v.viewsPerDay, NaN);
  return (Number.isFinite(vpdRaw) && vpdRaw > 0) ? vpdRaw : (num(v.views,0) / age);
}

function priority(v){
  const vpd = computeVpdFallback(v);
  const age = daysSince(v.publishedAt);
  const recencyScore = Math.max(0, (180 - age) / 180);
  const qs = num(v.quickScore, 0);
  let p = (vpd * 0.6) + (recencyScore * 40 * 0.2);
  if (vpd >= STRONG_VPD_BONUS) p += 5;
  if (qs) p += qs * QS_WEIGHT;
  return +p.toFixed(2);
}
function getChannelId(v){ return v.channel?.Id || v.channel?.id || v.channelId || ''; }

// ---------- 0) Normalize & keep best per video.id ----------
const bestById = new Map();
for (const it of items){
  const v = asVideoRow(it);
  if (!v || !v.id) continue;

  // hard drop (very old & cold)
  const age = daysSince(v.publishedAt);
  const vpd = computeVpdFallback(v);
  if (age > MAX_AGE_DAYS_HARD && vpd < MIN_VPD_BASELINE) continue;

  const prev = bestById.get(v.id);
  if (!prev || priority(v) > priority(prev.v)) bestById.set(v.id, { v, it });
}

// ---------- 1) Group by keyword ----------
const byKw = new Map();
for (const { v, it } of bestById.values()){
  const kw = getKwKey(v) || '(unknown)';
  if (!byKw.has(kw)) byKw.set(kw, []);
  byKw.get(kw).push({ v, it });
}

// ---------- 2) Per-keyword ranking with channel diversity penalty ----------
function rankWithinKeyword(arr){
  const seenChannels = new Set();
  const scored = arr.map(({v,it}) => {
    let p = priority(v);
    const ch = getChannelId(v);
    if (ch && seenChannels.has(ch)) p *= CHANNEL_REPEAT_PENALTY;
    return { v, it, p };
  });
  scored.sort((a,b)=> b.p - a.p);
  const ordered = [];
  const used = new Set();
  for (const s of scored){
    const ch = getChannelId(s.v);
    if (ch && used.has(ch)) { ordered.push(s); }
    else { ordered.push(s); if (ch) used.add(ch); }
  }
  return ordered;
}

// ---------- 3) Build floors and pool ----------
const floor = [];
const pool  = [];
for (const [kw, arr] of byKw.entries()){
  const ranked = rankWithinKeyword(arr);
  const floorCount = Math.min(MIN_PER_KEYWORD, ranked.length);
  for (let i=0; i<floorCount; i++) floor.push(ranked[i].it);
  for (let i=floorCount; i<ranked.length; i++) pool.push({ kw, it: ranked[i].it });
}

// ---------- 4) Fill globally ----------
const keptPerKw = new Map();
const kwOf = (it)=> getKwKey(asVideoRow(it)?.v || it.json || {});
for (const it of floor){ const kw = kwOf(it); keptPerKw.set(kw, (keptPerKw.get(kw)||0)+1); }

function pOf(it){ const v = asVideoRow(it); return v ? priority(v) : -1e9; }
pool.sort((a,b)=> pOf(b.it) - pOf(a.it));

const final = [...floor];
for (const {kw, it} of pool){
  if (final.length >= MAX_GLOBAL) break;
  const count = keptPerKw.get(kw) || 0;
  if (count >= MAX_PER_KEYWORD) continue;
  final.push(it);
  keptPerKw.set(kw, count+1);
}

// ---------- 5) Tag everything ----------
const keptIds = new Set(final.map(it => (asVideoRow(it)||{}).id).filter(Boolean));

for (const it of items){
  const g = ensureGate(it);
  const v = asVideoRow(it);
  if (!v) {
    g.keepGlobal = false;
    g.bucket = 'cut';
    g.reason.push('not a video');
    continue;
  }
  v.viewsPerDay = computeVpdFallback(v);
  g.priority = priority(v);
  g.keepGlobal = keptIds.has(v.id);
  g.bucket = g.keepGlobal ? 'keep' : 'cut';
  if (!g.keepGlobal){
    const age = daysSince(v.publishedAt);
    if (age > MAX_AGE_DAYS_HARD && v.viewsPerDay < MIN_VPD_BASELINE) g.reason.push('very old & cold');
  }
}

// Return all (tagged). Downstream, filter keepGlobal==true if desired.
return items;

function iso8601ToSeconds(iso) {
  const m = (iso || '').match(/PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+)S)?/);
  if (!m) return 0;
  const h = parseInt(m[1] || '0', 10);
  const mm = parseInt(m[2] || '0', 10);
  const s = parseInt(m[3] || '0', 10);
  return h*3600 + mm*60 + s;
}

const SHORT_210 = 210; // 3:30
const SHORT_60  = 60;

const store = $getWorkflowStaticData('global');
store.videoCache = store.videoCache || {};

const now = Date.now();
const out = [];

for (const item of items) {
  const id = item.json.id || item.json.videoId;
  const sn = item.json.snippet || {};
  const cd = item.json.contentDetails || {};
  const st = item.json.statistics || {};

  const secs = iso8601ToSeconds(cd.duration);
  const publishedAt = (sn.publishedAt || '').slice(0, 10);
  const ageDays = Math.max(
    1,
    Math.floor((Date.now() - new Date(publishedAt || '1970-01-01').getTime()) / 86400000)
  );

  const views = Number(st.viewCount || 0);
  const viewsPerDay = views / ageDays;

  // passthrough from upstream if present (so later nodes can use it)
  const passthruKeys = [
    'keyword','parentTopic','rankScore','quickScore','source','query'
  ];
  const passthru = {};
  for (const k of passthruKeys) {
    if (item.json[k] !== undefined) passthru[k] = item.json[k];
  }

  const normalized = {
    id,
    title: sn.title || '',
    channelId: sn.channelId || '',
    publishedAt,
    durationSeconds: secs,
    isUltraShort: secs <= SHORT_60,
    isShort: secs <= SHORT_210,
    views,
    viewsPerDay: Number.isFinite(viewsPerDay) ? +viewsPerDay.toFixed(2) : 0,
    lastFetched: now,
    ...passthru,
  };

  store.videoCache[id] = normalized; // upsert cache
  out.push({ json: normalized });
}

return out;

// Code node — Pre-keyword Gate (light prune + tagging, NO global caps)

const SOFT_PER_KEYWORD = 20;   // keep at most this many per keyword branch
const MAX_AGE_DAYS_HARD = 730; // drop if older than 2y AND cold
const COLD_VPD_FLOOR = 0.2;    // if below and very old, drop

const num = (v,d=0)=>{ const n=Number(v); return Number.isFinite(n)?n:d; };
const daysSince = (iso)=>{ const d=new Date(iso||0); return isNaN(d)?9e9:Math.max(0,(Date.now()-d)/86400000); };
const canon = (s)=> String(s||'').toLowerCase().replace(/[^\p{L}\p{N}\s]+/gu,' ').replace(/\s+/g,' ').trim();

function priority(x){
  // simple, fast priority; your global gate will do the “real” budget allocation
  const vpd = num(x.viewsPerDay, 0);
  const age = daysSince(x.publishedAt);
  const recency = Math.max(0, (180 - age) / 180); // ~6 months scale
  const qs = num(x.quickScore, 0);
  let p = (vpd * 0.6) + (recency * 40 * 0.2); // recency scaled into ~0–40
  if (vpd >= 12) p += 5;                      // small bump for strong momentum
  if (qs) p += qs * 20;                       // boost from autosuggest quality
  return +p.toFixed(2);
}

const byId = new Map();
for (const it of items) {
  const x = { ...(it.json||{}) };

  // guard: must have id & title
  const id = x.id || x.video?.id;
  const title = x.title || x.video?.title || '';
  if (!id || !title) continue;

  // unify fields
  x.title = title;
  x.publishedAt = x.publishedAt || x.video?.publishedAt || null;
  x.views = num(x.views ?? x.video?.views, 0);
  x.likeCount = num(x.likeCount ?? x.video?.likeCount, 0);
  x.commentCount = num(x.commentCount ?? x.video?.commentCount, 0);
  x.durationSeconds = num(x.durationSeconds ?? x.video?.durationSeconds, 0);

  // compute VPD fallback
  const ageDays = Math.max(1, Math.floor(daysSince(x.publishedAt)) || 1);
  const vpdRaw = num(x.viewsPerDay ?? x.video?.viewsPerDay, NaN);
  x.viewsPerDay = Number.isFinite(vpdRaw) && vpdRaw > 0 ? vpdRaw : (x.views / ageDays);

  // attach quickScore if only on keyword payload
  x.quickScore = num(x.quickScore ?? x.keyword?.quickScore, 0);

  // hard drop: very old AND cold
  if (ageDays > MAX_AGE_DAYS_HARD && x.viewsPerDay < COLD_VPD_FLOOR) continue;

  // prefer higher priority if same id seen
  const prev = byId.get(id);
  if (!prev || priority(x) > priority(prev)) byId.set(id, x);
}

// sort by priority, de-dupe near-duplicate titles per keyword
const arr = Array.from(byId.values());
const seenTitleStem = new Set();
const sorted = arr
  .sort((a,b) => priority(b) - priority(a))
  .filter(v => {
    const kw = (v.keyword?.keyword || v.keyword || v.parentTopic || '(unknown)').toString();
    const stem = `${kw}::${canon(v.title).replace(/\b(20\d{2}|19\d{2}|v?\d+(\.\d+)*)\b/g,'')}`;
    if (seenTitleStem.has(stem)) return false;
    seenTitleStem.add(stem);
    return true;
  })
  .slice(0, SOFT_PER_KEYWORD)
  .map((x, idx) => ({
    json: {
      ...x,
      gate: {
        index: idx,
        priority: priority(x),
        passBasic: true,
        keepPerKeyword: true,   // tagged as kept for this keyword branch
        keepGlobal: false,      // global gate decides later
        reason: []
      },
      _gate_meta: { id: x.id, key: (x.keyword?.keyword || x.keyword || '(unknown)') }
    }
  }));

return sorted;

return items.map(it => {
  const j = it.json;
  return {
    json: {
      keyword: j.keyword,
      winnerTitle: j.winner?.title,
      winnerUrl: j.winner?.url,
      groupStats: j.groupStats,
      recommend: j.recommend,
      keywordFit: j.keywordFit,
      parentTopic: j.parentTopic
    }
  }
});

// n8n Code node — Normalize to real videos ONLY (drop channels/playlists and fix IDs)

const num = (v,d=0)=>{ const n=Number(v); return Number.isFinite(n)?n:d; };
const daysSince = (iso)=>{ const d=new Date(iso||0); return isNaN(d)?9e9:Math.max(0,(Date.now()-d)/86400000); };
const isoToSecs = (iso) => {
  const m = (iso || '').match(/PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+)S)?/);
  if (!m) return 0; const h=+m[1]||0, mm=+m[2]||0, s=+m[3]||0; return h*3600+mm*60+s;
};
const isVideoId = (s)=> typeof s === 'string' && /^[A-Za-z0-9_-]{11}$/.test(s);

// Try to extract a videoId from any shape
function getVideoId(j){
  if (isVideoId(j?.video?.id)) return j.video.id;                // your normalized video
  if (j?.id?.kind === 'youtube#video' && isVideoId(j.id.videoId)) return j.id.videoId; // search.list
  if (isVideoId(j?.id) && j?.kind === 'youtube#video') return j.id;                     // videos.list (rare shape in n8n)
  if (isVideoId(j?._gate_meta?.id)) return j._gate_meta.id;       // you carried it earlier
  return null;
}

function pick(obj, ...paths){
  for (const p of paths){
    const v = p.split('.').reduce((a,k)=>a?.[k], obj);
    if (v !== undefined && v !== null && v !== '') return v;
  }
  return undefined;
}

const out = [];
for (const it of items){
  const j = it.json || {};
  const videoId = getVideoId(j);
  if (!videoId) continue;                       // not a video → skip

  // guard against channel/playlist kinds
  if (j.kind && j.kind !== 'youtube#video') {
    // sometimes search.list rows don’t carry kind at top-level; rely on videoId presence instead
    // if kind explicitly says channel/playlist, skip
    continue;
  }

  const title         = pick(j, 'video.title', 'snippet.title', 'title') || '';
  const publishedAt   = pick(j, 'video.publishedAt', 'snippet.publishedAt', 'publishedAt') || null;
  const durationSecs  = num(pick(j, 'video.durationSeconds'), 0) || isoToSecs(pick(j, 'contentDetails.duration'));
  const views         = num(pick(j, 'video.views', 'statistics.viewCount', 'views'), 0);
  const likeCount     = num(pick(j, 'video.likeCount', 'statistics.likeCount', 'likeCount'), 0);
  const commentCount  = num(pick(j, 'video.commentCount', 'statistics.commentCount', 'commentCount'), 0);
  const channelId     = pick(j, 'video.channelId', 'snippet.channelId', 'channelId') || pick(j, 'channel.Id', 'channel.id');
  const channelName   = pick(j, 'channel.Name', 'snippet.channelTitle', 'channelTitle');
  const subs          = num(pick(j, 'channel.subscriberCount', 'statistics.subscriberCount'), 0);

  const keyword       = pick(j, 'keyword.keyword', 'keyword') || null;
  const parentTopic   = pick(j, 'keyword.parentTopic', 'parentTopic') || null;
  const quickScore    = num(pick(j, 'quickScore', 'keyword.quickScore'), 0);
  const viewsPerDayIn = num(pick(j, 'video.viewsPerDay', 'viewsPerDay'), NaN);
  const ageDays       = Math.max(1, Math.floor(daysSince(publishedAt)) || 1);
  const viewsPerDay   = Number.isFinite(viewsPerDayIn) && viewsPerDayIn > 0 ? viewsPerDayIn : (views / ageDays);

  out.push({
    json: {
      // canonical video shape downstream
      id: videoId,
      title,
      channelId: channelId || undefined,
      channel: { Id: channelId || undefined, Name: channelName || undefined, subscriberCount: subs },
      publishedAt,
      durationSeconds: durationSecs,
      views,
      likeCount,
      commentCount,
      viewsPerDay,
      quickScore,
      keyword,
      parentTopic,

      // keep any existing gate tag, but reset bucket; the global gate will decide
      gate: {
        ...(j.gate || {}),
        keepGlobal: false,
        bucket: 'pending'
      },

      // helpful provenance (optional)
      _src: j._src || 'normalized',
    }
  });
}

return out;
